import React, { useState } from "react";
import Footer from "../components/shared/Footer";
import { data } from "../data/data";
import Card from "../components/shared/Card";
import { FaSearch } from "react-icons/fa";

const Menu = () => {
  const [input, setInput] = useState("");
  const [category, setCategory] = useState("");

  const setFoodItems = (input, category) => {
    let tmpData = data;
    if (category !== "") {
      tmpData = tmpData.filter((item) => item.category === `${category}`);
    }
    if (input !== "") {
      tmpData = tmpData.filter((item) =>
        item.name.toLowerCase().includes(`${input.toLowerCase()}`)
      );
    }
    return tmpData;
  };
  const foodItems = setFoodItems(input, category);
  const foodCategories = data
    .map((val) => val.category)
    .reduce((accum, val) => {
      if (!accum.includes(val)) {
        accum.push(val);
      }
      return accum;
    }, []);
  console.log(`food items: ${foodItems}`);

  // const getFoodItems =
  //   // input === '' ? data : data.filter((item) => item.name.toLowerCase() === `${input.toLowerCase()}`)
  //   input === '' ? data : data.filter((item) => item.name.toLowerCase().includes(`${input.toLowerCase()}`))

  return (
    <>
      <div>
        <div className="search-bar flex-center">
          <div className="search-input">
            <input
              placeholder="Type your search..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <FaSearch id="input-icon" />
          </div>
        </div>
        <div className="menu-header flex-center">
          <h2 className="popular-hdr-text">Popular Cuisines </h2>
          <div className="popular-btn flex-se">
            {data.map((item) => (
              <button
                onClick={() => setCategory(item.category)}
                className="main-btn btn "
              >
                {item.category} (
                {data.reduce(
                  (accumulator, val) =>
                    val.category === `${item.category}`
                      ? accumulator + 1
                      : accumulator,
                  0
                )}
                )
              </button>
            ))}
            {/* <button onClick={() => setCategory('burger')} className='btn main-btn'>
              Burger ()
            </button>
            <button onClick={() => setCategory('pizza')} className='btn main-btn'>
              Pizza ()
            </button>
            <button onClick={() => setCategory('pasta')} className='btn main-btn'>
              Pasta ()
            </button> */}
            <button onClick={() => setCategory("")} className="btn main-btn">
              All ({data.length})
            </button>
          </div>
        </div>

        <div className="menu-container">
          {foodItems.map((filterItems) => (
            <Card {...filterItems} />
          ))}
        </div>
      </div>

      <Footer />
    </>
  );
};

export default Menu;
